//div[@class="fulllistall"]/mip-showmore/*<js>
//按卷分隔
sub=String(result).slice(1,-1).split(/, (?=<dt class="volume")/);
sm=java.getString("class.bookinfo@h1@text",false);
sm=String(sm).replace(/\s/g,' ');
//显示卷名
var opts = {
  showVolName: false
};

if(sub.length>=1)for(var i=0;i<sub.length;i++){
//捕获卷名
jm=sub[i].match(/\s*([^<>\n]+)\s*<\/dt/);
if(!jm)continue;
sub[i]=sub[i].replace(/">(.*)<\/a/g,(opts.showVolName ? '">🍥['+jm[1]+']🍥$1<\/a':'">'+ ('').padStart(3, "\u2000")+'$1<\/a'));

//按卷分块
sub[i]='<dd><a href="https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+sm+'\\'+jm[1]+'">♦['+jm[1]+']♦</a></dd>'+sub[i];

}
//合并结果
sub.join("")
</js>a

