.sear li
<js>
result.toArray().slice(0,3).map(o=>{
    var url = baseUrl.match(/(.+\/\/.+?\/)/)[1] + o.select('a').attr('href');
    var J = org.jsoup.Jsoup.parse(java.ajax(url));
    var o = selector =>String(J.select(selector).text());
    return {
        name: o('h1').replace(/\s.*/,''),
        author: o('.cl1 ul').match(/作者：(.*)\s大小/)[1],
        kind: o('.cl1 ul').match(/类别：(.*)\s作者/)[1],
        wordCount: o('.cl1 ul').match(/大小：(.*)\s状态/)[1],
        latest: o('.cl1 ul').match(/入库时间：(.*)\s在线/)[1],
        intro: o('.jj'),
        cover: J.select('.cl1 img').attr('src'),
        url: url,
    }
});
</js>

ruleToc.chapterList:
@js:list = [];
if (baseUrl.match(/id/)) {
    p = result.match(/=(\d+)">最后1页/)[1];
    for (i = 0; i <= p; i++) {
        list.push({
            n: '第' + [i + 1] + '页',
            u: baseUrl.replace(/$/, '&yeshu=' + i )
        })
    }
} else list.push({
    n: '空',
    u: ''
});
